var MemFS = require('../memfs');
var test = require('../test');
var fs = new MemFS();
var should = require('should');

describe('memfs', function() {
    describe('resolve', function() {
        it('should absolute path work', function() {
            should(fs.resolve('/abc')).be.exactly('/abc');
        });

        it('should relative path work', function() {
            should(fs.resolve('abc')).be.exactly('/abc');
        });

        it('should ../ work', function() {
            should(fs.resolve('abc/def/../test')).be.exactly('/abc/test');
        });
    });

    describe('_getByPath', function() {
        it('should root exist', function() {
            var node = fs._getByPath('/');
            should(node).be.ok;
        });

        it('should none existance not exist', function() {
            var node = fs._getByPath('hello/world');
            should(node).be.exactly(null);
        });
    });

    test(fs);
});